func getNewApi() (chain.ChainAPI, error) {
	ctrMap := map[chain.ContractName]string{
		chain.BTS:          "0x7a6Cad739314adB23D752379e6A0699755Baa8B8",
		chain.BTSPeriphery: "0xF8e9a3DF47eF5168EfD7aE1EAf61fa53b70a8687",
		chain.BMCPeriphery: "0x15FC0d621b7E717b9aC111048ABe88Ea8dF5794c",
	}

	l := log.New()
	log.SetGlobalLogger(l)
	rx, err := NewApi(l, &chain.Config{
		Name:              chain.SNOW,
		URL:               RPC_URI,
		ContractAddresses: ctrMap,
		NativeTokens:      []string{},
		WrappedCoins:      []string{"btp-0x2.icon-ICX", "btp-0x2.icon-sICX", "btp-0x2.icon-bnUSD", "btp-0x61.bsc-BNB", "btp-0x61.bsc-BUSD", "btp-0x61.bsc-USDT", "btp-0x61.bsc-USDC", "btp-0x61.bsc-BTCB", "btp-0x61.bsc-ETH", "btp-0x2.near-NEAR"},
		NativeCoin:        "btp-0x228.snow-ICZ",
		NetworkID:         "0x228.snow",
		GasLimit:          make(map[chain.GasLimitType]uint64),
	})
	if err != nil {
		return nil, err
	}
	return rx, nil
}

func TestBMCParseMessage(t *testing.T) {
	rcptJSON := ""
	rcpt := &ethTypes.Receipt{}
	rcptBytes, err := hex.DecodeString(rcptJSON)
	require.NoError(t, err)
	err = rcpt.UnmarshalJSON(rcptBytes)
	require.NoError(t, err)
	ctrMap := map[chain.ContractName]string{
		chain.BTS:          "0x049a7Ef08e4f4D54Db764cA141f585b79C0310c7",
		chain.BTSPeriphery: "0x5003101271147Fcb6A4c79EE2807C2571e8c6dC7",
		chain.BMCPeriphery: "0x855183AefDA98Cd10Cf31488e26fF599685625a7",
	}
	parser, err := NewParser(RPC_URI, ctrMap)
	if err != nil {
		t.Fatal(err)
	}
	res, _, err := parser.parseMessage(rcpt.Logs[0])
	if err != nil {
		t.Fatal(err)
	}
	fmt.Println(res)
}

